mapper.select:
select
  host_id, host_name, host_ip, host_cpu_number, host_os, host_status
from mydocker.host_entity


        and host_id = #{hostId}


        and host_name = #{hostName}


        and host_ip = #{hostIp}


        and host_cpu_number = #{hostCpuNumber}


        and host_os = #{hostOs}


        and host_status = #{hostStatus}
